ThemeStore.DevicePreview = (function() {
    var DevicePreview = function() {

        this.$nodes = {
            previewLink: $('.theme-preview-link'), // on theme single page
            previewFrameContainer: $('#PreviewFrameContainer'),
            desktopPreview: $('#DesktopPreview'),
            mobilePreview: $('#MobilePreview'),
            allPreviewButtons: $('.device-preview-controls__toggle')
        };

        if (this.$nodes.previewLink) {
            this.iosPreviewLinkHandling();
        };

        if (!this.$nodes.previewFrameContainer.length) {
            return;
        }

        this.setFrameSize = _.bind(this.setFrameSize, this);

        mobileCSS = {
            maxWidth: '385px',
            maxHeight: '550px',
            /* marginLeft: '-160px',*/
            /* marginTop: '-311px', */ // 284px + 27px (~half of nav height: 55px)
            top: '50%',
            left: '50%'
        };

        desktopCSS = {
            maxWidth: '100%',
            maxHeight: '100%',
            margin: '0',
            top: '0',
            left: '0'
        };

        this.$nodes.desktopPreview.on('click', { size: desktopCSS }, this.setFrameSize);
        this.$nodes.mobilePreview.on('click', { size: mobileCSS }, this.setFrameSize);

        if (/[?&]mobile/.test(location.href) && !$('html').hasClass('touch')) {
            this.$nodes.mobilePreview.triggerHandler('click');
        }

    };

    DevicePreview.prototype.iosPreviewLinkHandling = function() {
        // iOS handles iframes very poorly. Attempting to scroll a
        // fixed size iframe causes more UX problems than it solves.
        // Instead of going to the preview page, switch the demo link
        // href to the demo shop itself.
        var iOS = /iPad|iPhone|iPod/.test(navigator.userAgent);

        if (!iOS) {
            return;
        }

        this.$nodes.previewLink.each(function() {
            var $el = $(this);
            var demoUrl = $el.data('demo-url');
            $el.attr('href', '//' + demoUrl);
        });
    };

    DevicePreview.prototype.setFrameSize = function(evt) {
        evt.preventDefault();
        this.$nodes.allPreviewButtons.removeClass('device-preview-controls__toggle--active');
        $(evt.target).addClass('device-preview-controls__toggle--active');
        this.$nodes.previewFrameContainer.css(evt.data.size);
    };

    return DevicePreview;
})();